// public/sw.js
import { precacheAndRoute } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { NetworkFirst, StaleWhileRevalidate, CacheFirst } from 'workbox-strategies';

// Precache all assets generated by your build
precacheAndRoute(self.__WB_MANIFEST);

// Cache documents with NetworkFirst
registerRoute(
  ({ request }) => request.destination === 'document',
  new NetworkFirst({
    cacheName: 'documents-cache',
  })
);

// Cache scripts and styles with StaleWhileRevalidate
registerRoute(
  ({ request }) => request.destination === 'script' || request.destination === 'style',
  new StaleWhileRevalidate({
    cacheName: 'assets-cache',
  })
);

// Cache JSON files
registerRoute(
  /sentences_.*\.json/,
  new CacheFirst({
    cacheName: 'sentences-cache',
  })
);

// Push notification handler
self.addEventListener('push', (event) => {
  try {
    const data = event.data?.json();  // Parsing the push message data
    console.log('Push event data:', data);  // Log for debugging

    // Check for missing title and log it for debugging
    if (!data?.title) {
      console.error('Push notification data is missing a title:', data);
    }

    const title = data?.title || 'Burushaski Recording Reminder';
    const options = {
      body: data?.body || 'Time to record some sentences!',
      icon: '/icon-192.png',
      badge: '/icon-192.png',
      tag: 'burushaski-reminder',
      requireInteraction: true,
      actions: [
        {
          action: 'open',
          title: 'Open App',
        },
        {
          action: 'close',
          title: 'Dismiss',
        },
      ],
    };

    // Show notification
    event.waitUntil(self.registration.showNotification(title, options));
  } catch (error) {
    console.error('Error parsing push notification data:', error);
    // Log the raw event data for debugging
    console.error('Raw push event data:', event.data);
  }
});

// Handle notification clicks
self.addEventListener('notificationclick', (event) => {
  event.notification.close();

  if (event.action === 'open' || !event.action) {
    event.waitUntil(
      clients.matchAll({ type: 'window' }).then((clientList) => {
        for (const client of clientList) {
          if (client.url.includes('/') && 'focus' in client) {
            return client.focus();
          }
        }
        if (clients.openWindow) {
          return clients.openWindow('/dashboard');
        }
      })
    );
  }
});
